targets.expr:
avg_over_time((avg by (prover_group_id) (label_replace(
  (

      DCGM_FI_DEV_GPU_UTIL
      * on(pod) group_left(host_ip)
        sum by (pod, host_ip) (
          kube_pod_info{pod=~"dcgm-dcgm-exporter.*"}
        )
    )
    * on(host_ip) group_left(pod)
      sum by (pod, host_ip) (
        sum by (pod) (
          kube_pod_status_phase{pod=~"prover-gpu-fri-spec-.*", phase="Running"}
        )
        * on(pod) group_left(host_ip)
          sum by (pod, host_ip) (
            kube_pod_info{pod=~"prover-gpu-fri-spec.*"}
          )
  ),
  "prover_group_id", "$1", "pod", "^prover-gpu-fri-spec-([0-9]+)(-[0-9a-zA-Z-]+)?$"
)))[15m:])

avg_over_time((avg by (cluster, prover_group_id) (label_replace(
  (
      DCGM_FI_DEV_GPU_UTIL
      * on(pod) group_left(host_ip)
        sum by (pod, host_ip) (
          kube_pod_info{pod=~"dcgm-dcgm-exporter.*"}
        )
    )
    * on(host_ip) group_left(pod)
      sum by (pod, host_ip) (
        sum by (pod) (
          kube_pod_status_phase{pod=~"prover-gpu-fri-spec-.*", phase="Running"}
        )
        * on(pod) group_left(host_ip)
          sum by (pod, host_ip) (
            kube_pod_info{pod=~"prover-gpu-fri-spec.*"}
          )

  ),
  "prover_group_id", "$1", "pod", "^prover-gpu-fri-spec-([0-9]+)(-[0-9a-zA-Z-]+)?$"
)))[15m:])
